import './App.css';
import About from './components/About';
import Navbar from './components/Navbar';
import React, { useState } from 'react';
import TextForm from './components/TextForm';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
function App() {
  const [mode, setMode] = useState('light');  //whether dark mode enable or not

  const toggleMode = () => {
    if (mode === 'dark') {
      setMode('light')
      document.body.style.backgroundColor = 'white'
      document.title = 'TextUtile - Light Mode'
    }
    else {
      setMode('dark')
      document.body.style.backgroundColor = 'grey'
      document.title = 'TextUtile - Dark Mode'
      setInterval(() => {
        document.title = 'Hello downlode karo ese yr'
      }, 2000);
    }
  }
  return (
    <>
    <Router>
      <Navbar title="TextUtils" mode={mode} toggleMode={toggleMode} />
      <div className="container my-3">
      <BrowserRouter>
          <Route path="/about" element={<About/>}>
          </Route>
          <Route path="/">
          element={<TextForm heading="Enter your text" mode={mode} />}
          </Route>
      </BrowserRouter>
      </div>
    </Router>
    </>
  );
}

export default App;
