import React, { useState } from 'react';

export default function TextForm(props) {
    const handelUpCase = () => {
        // console.log("Upper case was Clicked" + text);
        let newText = text.toUpperCase()
        setText(newText);
    }
    const handelOnChange = (event) => {
        // console.log("on changed");
        setText(event.target.value);
    }
    const [text, setText] = useState('Enter text here');

    return (
        <>
            <div>
                <h1>{props.heading}</h1>
                <div className="mb-3">
                    <textarea className="form-control" value={text} onChange={handelOnChange} id="myBox" rows="8"></textarea>
                </div>
                <button className="btn btn-primary mx-1" onClick={handelUpCase}>Convert UpperCase</button>
                <button className="btn btn-primary mx-1" onClick={handelLoCase}>Convert LowerCase</button>
            </div>
            <div>
            <h2>Your Text summary</h2>
            <p>{text}</p>
            </div>
        </>
    );
}
